import React, { useState } from 'react';
import './Form.css';
import ReactFlagsSelect from 'react-flags-select';

function Form() {
    const [selected, setSelected] = useState('IN');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [feedback, setFeedback] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [rating, setRating] = useState('');

    const [nameError, setNameError] = useState('');
    const [emailError, setEmailError] = useState('');
    const [feedbackError, setFeedbackError] = useState('');
    const [phoneNumberError, setPhoneNumberError] = useState('');
    const [ratingError, setRatingError] = useState('');
    const [fieldError, setFieldError] = useState('');

    function isValid() {
        const errorMessage = 'Error: This is mandatory field';
        var ok= true;

        if(!name) {
            ok= false;
            setNameError(errorMessage);
        }
        else setNameError('');

        if(!email || !email.includes('@')){
            ok=false;
            setEmailError(errorMessage);
        }
        else setEmailError('');

        if(!feedback){
            ok=false;
            setFeedbackError(errorMessage);
        }
        else setFeedbackError('');

        if(phoneNumber.trim().length != 10 || Number(phoneNumber.trim()[0]) < 7
            ){
            ok=false;
            setPhoneNumberError(errorMessage);
        }
        else setPhoneNumberError('');

        if(!rating){
            ok=false;
            setRatingError(errorMessage);
        }
        else setRatingError('');

        return ok;
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if(isValid()){
            alert("Form Submitted Successfully");
        }
    }

    return (
        <div className="form">
            <div className="form__info">
                <p className="form__header">Aromatic Bar</p>
                <p className="form__description">We are committed to providing you with the best
                    dining experience possible, so we welcome your comments. Please fill
                    out this questionnaire. Thank you.
                </p>
            </div>

            <form className="form__container" onSubmit={handleSubmit}>

                <div className="form__container__col">
                    <div className="form__inputs">
                        <label for="customer__name">Customer's Name</label>
                        <input 
                            type="text" 
                            maxLength="50" 
                            name="customer__name"
                            value={name}
                            onChange={e => setName(e.target.value)}
                        />
                        <p className="form__error">{nameError}</p>
                    </div>
                    <div className="form__inputs">
                        <label for="customer__number">Mobile Number</label>
                        <div className="form__customer__number">
                            <ReactFlagsSelect
                                className="form__container__flag"
                                selected={selected}
                                onSelect={code => setSelected(code)}
                                showSelectedLabel={false}
                                showOptionLabel={false}
                            />
                            <input 
                                type="tel" 
                                pattern="[7-9]{1}[0-9]{9}" 
                                name="customer__number"
                                value={phoneNumber}
                                onChange={e => setPhoneNumber(e.target.value)}
                            />
                            <p className="form__error">{phoneNumberError}</p>
                        </div>
                    </div>

                    <div className="form__inputs">
                        <label for="customer__feedback__text">Customer's Feedback</label>
                        <textarea 
                            className="customer__feedback__text"
                            type="text" 
                            maxLength= "250"
                            name="customer__feedback__text"
                            value={feedback}
                            onChange={e => setFeedback(e.target.value)}
                        />
                        <p className="form__error">{feedbackError}</p>
                    </div>

                </div>
                <div className="form__container__col">
                    <div className="form__inputs">
                        <label for="customer__email__id">Customer's Email Id</label>
                        <input 
                            type="email"
                            name="customer__email__id"
                            value={email}
                            onChange={e => setEmail(e.target.value)}
                        />
                        <p className="form__error">{emailError}</p>
                    </div>

                    <p className="form__rate__our__food">Rate our Food</p>
                    <div className="form__rating">
                        <input 
                            type="radio" 
                            name="rate3"
                            value="Excellent"
                            checked={rating==="Excellent"}
                            onClick={e => setRating("Excellent")}
                        />
                        <label for="rate3">Excellent</label>
                        <input 
                            type="radio" 
                            name="rate2"
                            value="Good"
                            checked={rating==="Good"}
                            onClick={e => setRating("Good")}
                        />
                        <label for="rate2">Good</label>
                        <input 
                            type="radio" 
                            name="rate1"
                            value="Fair"
                            checked={rating==="Fair"}
                            onClick={e => setRating("Fair")}
                        />
                        <label for="rate1">Fair</label>
                        <input 
                            type="radio" 
                            name="rate0"
                            value="Bad"
                            checked={rating==="Bad"}
                            onClick={e => setRating("Bad")}
                        />
                        <label for="rate0">Bad</label>
                        {rating}
                    </div>
                    <p className="form__error">{ratingError}</p>
                </div>

                <input value="Submit" className="form__submit" type="submit" />

            </form>

        </div>
    )
}

export default Form;
